    {
                msg1.payload = 3;                                               // set operationmode to 3: DHW-Only
                msg2.payload = 1;                                               // Turn on Force DHW mode
                return [msg1, msg2];
    }

// START Sterilization cycle
if (msg.topic == 'Sterilization' && msg.payload == 1)                           
    {
                msg1.payload = 3;                                               // set operationmode to 3: DHW-Only
                msg2.payload = 1;                                               // Turn on Force DHW mode
                msg3.payload = 1;                                               // Turn on Force Sterilization mode
                return [msg1, msg2, msg3];
    }

////////////////////////////////////////////////////////////////////////////////
//                           END OF THE CYCLE                                 //
////////////////////////////////////////////////////////////////////////////////


// END DHW cycle (Automatic)
if (Force_DHW_State == 1 && Sterilization_State == 0 && DHW_Temp >= DHW_Target_Temp)
    {
                setTimeout(function(){
                    node.status({fill:"red", shape:"ring", text:""});
                    msg1.payload = 0;                                           // set operationmode to 0: HEAT-Only
                    node.send([msg1,null,null,null]);                                     // send msg1 only, other outputs are null
                    }, 5000);
                msg2.payload = 0;                                               // Turn off Force DHW mode
                return [null, msg2];
    }


// END Sterilization cycle (Automatic)
