// Automagische Stooklijn correctie (original by CurlyMo, translated to NodeRed by EdterBak)

var PV=global.get('T_woonkamer_BT'); // Temperatuurmeting van de ruimte
var SP=global.get('WoonkamerSP'); // Setpoint van de ruimte waar de stooklijnDelta voor gecorrigeerd zal worden
var Tb=global.get('T_buiten_darksky'); // Buitentemperatuur
var Z1_Heat_Curve_Target_High_Temp=global.get('Z1_Heat_Curve_Target_High_Temp'); // TOP29
var var_dT=(PV-SP); // als de waarde > 0 dan is het te warm. Als < dan is het te koud
var StooklijnDelta=26; // Startpunt voor alle onderstaande correcties

//
// Het is warmer binnen. Bij iedere if wordt er een -1 toegevoegd. Dus bij 1.7Â°C te warm zal de stooklijndelta -3 worden.
//
if (var_dT >= 0.3) // Het is iets te warm
    {
    StooklijnDelta = StooklijnDelta -1;
    }
if (var_dT >= 0.6) // Het is redelijk te warm
    {
    StooklijnDelta = StooklijnDelta -1;
    }
if (var_dT >= 1.5) // Het is veel te warm
    {
    StooklijnDelta = StooklijnDelta -1;
    }

if (var_dT < -0.2) // Het is iets te koud
    {
    StooklijnDelta = StooklijnDelta +1;
    }
if (Tb < Z1_Heat_Curve_Target_High_Temp -4)
    {
    if (var_dT < -0.4) // Het is nog iets kouder
        {
        StooklijnDelta = StooklijnDelta +1;
        }

    if (var_dT < -0.6) // Het is iets veel te koud
        {
        StooklijnDelta = StooklijnDelta +1;
        }
    }


msg.payload = StooklijnDelta;
return msg;

