/*
This function has the purpose to regularly check the actual SP from the heatpump against the calculated setpoint in from the dashboard/functions. 
Only when there is a deviation between the two, the new setpoint gets sent through MQTT to SET5.
*/


var ThreeWay_Valve_State=global.get('ThreeWay_Valve_State');                    // Get the global variable for current possition of the three way valve. ROOM or DHW mode. (0=HEAT / 1=DHW)

if (ThreeWay_Valve_State==1)                                                    // If state is 1 (DHW mode), do not do anything.
    {
        return null;
    }

else 
    {
    var SP_Final=global.get('SP_Final',"file");
    var current_sp=global.get('Main_Target_Temp');                              // Get the global variable for current possition of the three way valve. ROOM or DHW mode. (0=HEAT / 1=DHW)    
    var Correction_0_WAR_on_off=global.get('Correction_0_WAR_on_off',"file");
    var Correction_1_RTC_on_off=global.get('Correction_1_RTC_on_off',"file");
    var Correction_2_softstart_on_off=global.get('Correction_2_softstart_on_off',"file");


    if ((Correction_0_WAR_on_off + Correction_1_RTC_on_off + Correction_2_softstart_on_off) == 0 )    
                                                                                // Check 1, only when one of the functions is enabled. The sum of the states should be 1 or more, otherwise all functions are off.
            {
                return null;
            }


    else    {
                if (SP_Final ==  current_sp)                                    // Check 1, only if there is a difference between SP and current value, a signal should be sent.
                    {return null;}
                else
                    {msg.payload = SP_Final;
                    return msg;}
            }    
    }

