if (ThreeWay_Valve_State==1){msg.payload=0;return msg;}                         // If state is 1 (DHW mode), correction 0 is direclty exported to the message and the function ends here.
else{
//////////////////////////////////////////////////////////////////////////////
//////////////////////////////////////////////////////////////////////////////

var SP0_start       = global.get('SP_DIRECT_virt',"file");             // Get the global variable for SP_DIRECT_virt. Value is set through the dashboard

//////////////////////////////// WAR /////////////////////////////////////////

// This function replaces the virtual Ta Direct set from the dashboard. 

// Design example
//          OUTSIDE                 WATER
//          Temperature     >>      Temperature
//----------------------------------------------------------------------------
// Range 1
//          (OT1=)  20      >>  25  (=WT1)
// Range 2
//          (OT2=)  5       >>  31  (=WT2)
//----------------------------------------------------------------------------
//          (OT2=)  5       >>  31  (=WT2)
// Range 3
//          (OT3=)  -10     >>  37  (=WT3)
// Range 4
//----------------------------------------------------------------------------
var war_state       = global.get('Correction_0_WAR_on_off',"file");             // Get the global variable. Used to see if the WAR function is enabled.
var war_Tb          = global.get('T_outside',"file");                           // Outdoor temperature.
var SP1_war                                                                     // set variable

if (war_state==1 && war_Tb > -50)                                               // If the state is 1 (enabled), AND outside temperature contains a value.
                                                                                // Only if above conditions are met, we will go through the calculation.
    {
        var war_ot1         = global.get('war_ot1',"file");                     // Outside temperature high         (warm outside)
        var war_ot2         = global.get('war_ot2',"file");                     // Outside temperature intermediate (intermediate outside temperature)
        var war_ot3         = global.get('war_ot3',"file");                     // Outside temperature low          (cold outside)
        var war_wt1         = global.get('war_wt1',"file");                     // Water temperature low            (cold water)
        var war_wt2         = global.get('war_wt2',"file");                     // Water temperature intermediate   (intermediate water temperature)
        var war_wt3         = global.get('war_wt3',"file");                     // Water temperature high           (warm water)

            if (war_Tb => war_ot1) 
                {SP1_war = war_wt1;}                                            // Range 1

            if (war_Tb < war_ot1 && war_Tb > war_ot2)                           // Range 2
                {SP1_war = (((war_wt1-war_wt2)*((war_Tb-war_ot1)/(war_ot1-war_ot2)))+war_wt1);}

            if (war_Tb == war_ot2) {SP1_war = war_wt2;}                         // transition point 2|3

            if (war_Tb < war_ot2 && war_Tb > war_ot3)                           // Range 3
                {SP1_war = (((war_wt2-war_wt3)*((war_Tb-war_ot2)/(war_ot2-war_ot3)))+war_wt2);}

            if (war_Tb <= war_ot3)                                              // Range 4
                {SP1_war = war_wt3;}                                            

    SP1_war = Number(SP1_war).toFixed(0);
    SP1_war = parseFloat(SP1_war);
    }

else 
    {
        SP1_war = SP0_start;                                                    // Get the global variable for SP_DIRECT_virt. Value is set through the dashboard
    }



