////////////////////////////////////////////////////////////////////////////////

// The expected output of this function is in (-) Watts when there is power import from the grid towards your house.
//
//  EXAMPLES:
//  IF your P1 output is in (+) Watt, you need to use -1 instead of 1000 in the value below
//  IF your P1 output is in (-) Watt, you need to use 1 instead of 1000 in the value below
//
//  IF your P1 output is in (+) kiloWatt, you need to use -1000 in the value below
//  IF your P1 output is in (-) kiloWatt, you need to use 1000 in the value below

msg.payload = msg.payload * -1000; 
// msg.payload = msg.payload * 1000;                                              // This is just an example.

////////////////////////////////////////////////////////////////////////////////
//
// Do NOT remove this function completly. The output is a number, not text.
//
////////////////////////////////////////////////////////////////////////////////

msg.topic = "W";
return msg;

msg1 = {};
msg2 = {};
msg3 = {};
msg4 = {};
msg5 = {};
msg6 = {};

var CurrentTime = new Date().getTime();
var PreviousTime = context.get("PreviousTime");
var PreviousMessage = context.get("PreviousMessage");
var PreviousTopic = context.get("PreviousTopic");
var exit = false;

//  First run checks. If some variables are undefined, set current values and exit.
if (PreviousTime === undefined) {context.set('PreviousTime',CurrentTime);       exit = true;}
if (PreviousMessage === undefined) {context.set('PreviousMessage',msg.payload); exit = true;}
if (PreviousTopic === undefined)  {context.set('PreviousTopic',msg.topic);    exit = true;}

if (exit === true)        
    {    return null;            // This output will automatically be skipped, next calculation will be ok.

    }

else
    {
        var LastMessage = msg.payload;
        var LastTopic= msg.topic;

        TimeElapsed = ((CurrentTime - PreviousTime)/1000);
        context.set('PreviousTime',CurrentTime);
        context.set('PreviousMessage',msg.payload);
        context.set('PreviousTopic',msg.topic);
        msg3.last = {"lastTime":CurrentTime,"LastMessage":LastMessage,"LastTopic":LastTopic};
        msg3.Previous = {"PreviousTime":PreviousTime,"PreviousMessage":PreviousMessage,"PreviousTopic":PreviousTopic};


        msg1.payload = (TimeElapsed + "  Seconds");
        temp = parseFloat(TimeElapsed.toFixed(0));
        msg2.payload = temp;
        msg3.payload = {"Count":temp,"Units":"Seconds"}; 
        msg5.payload = (PreviousMessage * ( temp / 3600000 ));                  //  Calculation to Wh portion
        Number(msg5.payload).toFixed(4);
        msg5.topic = 'GRID power consumption kWh';                               // msg5 = latest kWh part.


    ///////////////////////////////////////////////////////////////////////////
    // Calculation from kWh part > total kWh of today
        var total_kwh_import = flow.get("total_kwh_import","file");
        if (total_kwh_import === undefined)        {total_kwh_import = 0;}

        msg6.payload = msg5.payload + total_kwh_import;                                // msg6 = kWh total from the start of operation
        msg6.topic = 'GRID power consumption kWh';

        flow.set('total_kwh_import',msg6.payload,"file");

    ////////////////////////////////////////////////////////////////////////////
        return msg6;

    }

var DHW_Current                     = global.get('DHW_Target_Temp',"file");
var DHW_SP_HIGH                     = global.get('DHW_SP_HIGH',"file");
var SolarFunction_active            = global.get('SolarFunction_active',"file");
var Operating_Mode_State            = global.get('Operating_Mode_State',"file");
var ThreeWay_Valve_State            = global.get('ThreeWay_Valve_State');
var new_Operating_Mode_State;

if (DHW_Current >= DHW_SP_HIGH && SolarFunction_active == 1 && ThreeWay_Valve_State == 0)
    {


            let msg1={};        // DHW_Current
            let msg2={};        // SolarFunction_active
            let msg3={};        // new_Operating_Mode_State

            if (Operating_Mode_State == 4){new_Operating_Mode_State = 0;} // remove DHW modus
            if (Operating_Mode_State == 5){new_Operating_Mode_State = 1;} // remove DHW modus
            if (Operating_Mode_State == 6){new_Operating_Mode_State = 2;} // remove DHW modus
            if (Operating_Mode_State == 8){new_Operating_Mode_State = 7;} // remove DHW modus

            if (Operating_Mode_State == 0){new_Operating_Mode_State = 0;} // do nothing, prevent undefined
            if (Operating_Mode_State == 1){new_Operating_Mode_State = 1;} // do nothing, prevent undefined
            if (Operating_Mode_State == 2){new_Operating_Mode_State = 2;} // do nothing, prevent undefined
            if (Operating_Mode_State == 3){new_Operating_Mode_State = 3;} // do nothing, prevent undefined
            if (Operating_Mode_State == 7){new_Operating_Mode_State = 7;} // do nothing, prevent undefined

// Operating mode (0=Heat only, 1=Cool only, 2=Auto(Heat), 3=DHW only, 4=Heat+DHW, 5=Cool+DHW, 6=Auto(Heat)+DHW, 7=Auto(Cool), 8=Auto(Cool)+DHW)

            msg1.payload    = DHW_Current;
            msg1.topic      = "DHW_Current";

            msg2.payload    = 0;                                                   // get rid of block
            msg2.topic      = "SolarFunction_active";

            msg3.payload    = new_Operating_Mode_State;
            msg3.topic      = "new_Operating_Mode_State";


            return [msg1,msg2,msg3];

    }

else
    {
        return [null,null,null];

    }

global.set("SolarFunction_active",msg.payload,"file");
let msg1={};
msg1.topic = "NR Solar Function";
msg1.payload = "DHW temperature reached DHW SP High.";

return msg1;
