        if (SP_Final>HEAT_SP_upper_limit)   {SP_Final   =HEAT_SP_upper_limit}
        if (SP_Final<20)                    {SP_Final   =20;}

//*****************************************************************************

        msg1.payload    = SP_Final;                             msg1.topic  = "SP_Final";
        global.set('SP_Final',SP_Final,"file")

        msg99.payload   = "New final SP: " + SP_Final + " C";  msg99.topic = "Result Functions";        // ToLog



//*****************************************************************************

        if (SP_Final ==  current_sp)                                            // Check only if there is a difference between SP and current value, a signal should be sent.
            {
                return [msg1,null,null];
            }
        else
            {
                return [msg1,msg99,null];
            }
    }

