////////////////////////////////////////////////////////////////////////////
////////////////////////// RTC - ON/OFF function ///////////////////////////
//
//
//  This function has the goal to act when the temperature in the room is too high.
//  It can either turn off the heatpump, or take the [heat]-mode out of the current operating mode.
//  
var Operating_Mode              =global.get('Operating_Mode_State',"file");
//  Operating mode (0=Heat only, 1=Cool only, 2=Auto(Heat), 3=DHW only, 4=Heat+DHW, 5=Cool+DHW, 6=Auto(Heat)+DHW, 7=Auto(Cool), 8=Auto(Cool)+DHW)
//
var rtc_state                   =global.get('Correction_1_RTC_on_off',"file");         // Get the global variable. Used to see if the RTC function is enabled.
    if (rtc_state===undefined)  {rtc_state=0;}
//
var F_RTC_on_off2               =global.get('F_RTC_on_off2',"file");
    if (F_RTC_on_off2 === undefined)    {F_RTC_on_off2=0;}         // Get the global variable. Used to see if the RTC function is enabled through GUI.

if (rtc_state == 0 || F_RTC_on_off2 == 0 || Operating_Mode === undefined || Operating_Mode==1 || Operating_Mode==5 || Operating_Mode==7 || Operating_Mode==8)     //  First IF
    {   
        return null;
    }

else 
    {   
        // Declaration of variables including their values.    
        var rtc_PV          = global.get('T_room');                             // Get the global variable. THe room temperature for RoomTemperatureCompensation you want to control.
        var rtc_SP          = global.get('WoonkamerSP',"file");                 // Virtual target temperature setpoint for RTC. Set in Dashboard. 
        var rtc_dT          = (rtc_PV-rtc_SP);                                  // local variable for calculated temperature difference. If > 0 it is too warm, If < 0 it is too cold. 

        var Heatpump_State  = global.get('Heatpump_State',"file");              // get current state of the heatpump. on or off (1 or 0)

        // below are the variables containing the temperature ranges.
        var rtc_off         = global.get('rtc_off',"file");                     // Range turn heatpump off
            if (rtc_off === undefined)  
                {
                    rtc_off = 10;
                    global.set('rtc_off',10,"file");
                }
        var rtc_on          = global.get('rtc_on',"file");                      // Range turn heatpump on
            if (rtc_on === undefined)   
                {
                    rtc_on = -10;
                    global.set('rtc_on',-10,"file");
                }



        var F_rtc_on_off_active             = global.get('F_rtc_on_off_active',"file");
            if (F_rtc_on_off_active===undefined)    {F_rtc_on_off_active=0;}
        var F_stored_next_operating_mode    = global.get('F_stored_next_operating_mode',"file");

        let msg1    ={};        //  pin 1 - Output to power pump on/off 
        let msg2    ={};        //  pin 2 - Output to Operating Mode
        let msg99   ={};        //  pin 3 - Messages to Log
            msg99.topic     = "Function RTC";
        //*****************************************************************************
        // Operating mode (0=Heat only, 1=Cool only, 2=Auto(Heat), 3=DHW only, 4=Heat+DHW, 5=Cool+DHW, 6=Auto(Heat)+DHW, 7=Auto(Cool), 8=Auto(Cool)+DHW)
        if (rtc_off <= rtc_on)
            {
                node.warn('RTC function: Current OFF temperature is lower than the ON temperature. You must correct this !! ');
                msg99.payload   = "ERROR: Current OFF temperature is lower than the ON temperature. You must correct this !!"; 
                return [null,null,msg99];
            }
        //*****************************************************************************

        if (rtc_dT > rtc_off && Heatpump_State == 1 && F_rtc_on_off_active == 0 && Operating_Mode != 3)                    // It is waaaaaay way too hot, heatpump is on and function has not already triggered.
            {
                //  Operating mode (0=Heat only, 1=Cool only, 2=Auto(Heat), 3=DHW only, 4=Heat+DHW, 5=Cool+DHW, 6=Auto(Heat)+DHW, 7=Auto(Cool), 8=Auto(Cool)+DHW)
                if (Operating_Mode == 0 || Operating_Mode == 2)
                                            {
                                                global.set('F_rtc_on_off_active',1,"file");
                                                msg1.payload=0;
                                                msg1.topic="new_heatpump_power";
                                                msg99.payload = "on/off - Turn off power";
                                                return [msg1,null,msg99]; 
                                            }

                if (Operating_Mode == 4 || Operating_Mode == 6)    
                                            {
                                                if (F_stored_next_operating_mode === undefined)
                                                    {
                                                        global.set('F_stored_next_operating_mode',Operating_Mode,"file");
                                                    }
                                                global.set('F_rtc_on_off_active',1,"file");
                                                msg2.payload=3;
                                                msg2.topic="new_Operating_Mode";
                                                msg99.payload = "on/off - Operating mode from " + Operating_Mode + " to 3";
                                                return [null,msg2,msg99]; 
                                            }

            }
        //*****************************************************************************
        if (rtc_dT < rtc_on && F_rtc_on_off_active == 1)                             // It is cold enough again, turn on heatpump
            {
                if (Heatpump_State == 0)    {
                                                global.set('F_rtc_on_off_active',0,"file");
                                                msg1.payload=1;
                                                msg1.topic="new_heatpump_power";
                                                msg99.payload = "on/off - Turn on power";
                                                return [msg1,null,msg99]; 
                                            }

                if (Heatpump_State == 1)    {
                                                global.set('F_rtc_on_off_active',0,"file");
                                                msg2.payload = F_stored_next_operating_mode;
                                                msg2.topic="new_Operating_Mode";
                                                msg99.payload = "on/off - Restored to OM " + F_stored_next_operating_mode;

                                                global.set('F_stored_next_operating_mode',undefined,"file");
                                                return [null,msg2,msg99]; 
                                            }

            }  
//******************************************************************************
    }

