// >> trigger 1
// DHW / Sterilisation? This is defined in the topic
// Topic: DHW or topic: Sterilization
// payload 1 > initiate sequence. 

// Operating mode (0=Heat only, 1=Cool only, 2=Auto(Heat), 3=DHW only, 4=Heat+DHW, 5=Cool+DHW, 6=Auto(Heat)+DHW, 7=Auto(Cool), 8=Auto(Cool)+DHW)

// Declaration of the variables. 
let msg1={}, msg2={}, msg3={}, msg4={};
// msg1     Output towards Operation mode
// msg2     Output towards Force DHW mode on/off
// msg3     output towards Force Sterilization on/off
// msg4     empty


var ThreeWay_Valve_State=global.get('ThreeWay_Valve_State');                    // valvue position

var DHW_Temp=global.get('DHW_Temp');                                            // Current temperature of DHW vessel. 
var DHW_Target_Temp=global.get('DHW_Target_Temp',"file");                       // Programmed DHW target temperature
var Force_DHW_State=global.get('Force_DHW_State');                              // Current DHW state (on / off). 

var Operating_Mode_State=global.get('Operating_Mode_State',"file");             // get Current OperationMode
var new_OM;                                                                     // create empty variable.

var Sterilization_State=global.get('Sterilization_State');                      // Current sterilization state (on / off)
var Sterilization_Temp=global.get('Sterilization_Temp',"file");                 // Programmed sterilization target temperature
var DHW_treshold=global.get('DHW_treshold',"file");                             // Programmed treshold DHW tank temperature set from GUI
////////////////////////////////////////////////////////////////////////////////
//                           START OF THE CYCLE                               //
////////////////////////////////////////////////////////////////////////////////

// START Force DHW cycle
if (msg.topic == 'DHW' && msg.payload == 1 && DHW_Temp < DHW_treshold)          // There is a temperature condition to start DHW heating or not. Change to your own desire.
    {
                // Operating mode (0=Heat only, 1=Cool only, 2=Auto(Heat), 3=DHW only, 4=Heat+DHW, 5=Cool+DHW, 6=Auto(Heat)+DHW, 7=Auto(Cool), 8=Auto(Cool)+DHW)
                if (Operating_Mode_State==0){new_OM = 4;}
                if (Operating_Mode_State==1){new_OM = 5;}
                if (Operating_Mode_State==2){new_OM = 6;}
                if (Operating_Mode_State==3){new_OM = 3;}
                if (Operating_Mode_State==4){new_OM = 4;}
                if (Operating_Mode_State==5){new_OM = 5;}
                if (Operating_Mode_State==6){new_OM = 6;}
                if (Operating_Mode_State==7){new_OM = 8;}
                if (Operating_Mode_State==8){new_OM = 8;}


                msg1.payload = new_OM;                                          // set operationmode to add DHW mode
                msg2.payload = 1;                                               // Turn on Force DHW mode
                if (Operating_Mode_State!=new_OM)
                    {return [msg1, msg2];}
                else {return [null, msg2];}
    }

// START Force Sterilization cycle
if (msg.topic == 'Sterilization' && msg.payload == 1)                           
    {
                // Operating mode (0=Heat only, 1=Cool only, 2=Auto(Heat), 3=DHW only, 4=Heat+DHW, 5=Cool+DHW, 6=Auto(Heat)+DHW, 7=Auto(Cool), 8=Auto(Cool)+DHW)
                if (Operating_Mode_State==0){new_OM = 4;}
                if (Operating_Mode_State==1){new_OM = 5;}
                if (Operating_Mode_State==2){new_OM = 6;}
                if (Operating_Mode_State==3){new_OM = 3;}
                if (Operating_Mode_State==4){new_OM = 4;}
                if (Operating_Mode_State==5){new_OM = 5;}
                if (Operating_Mode_State==6){new_OM = 6;}
                if (Operating_Mode_State==7){new_OM = 8;}
                if (Operating_Mode_State==8){new_OM = 8;}


                msg1.payload = new_OM;                                               // set operationmode to 3: DHW-Only
                msg2.payload = 1;                                               // Turn on Force DHW mode
                msg3.payload = 1;                                               // Turn on Force Sterilization mode

                if (Operating_Mode_State!=new_OM)
                    {return [msg1, msg2, msg3];}
                else
                    {return [null, msg2, msg3];}


    }

////////////////////////////////////////////////////////////////////////////////
//                           END OF THE CYCLE                                 //
////////////////////////////////////////////////////////////////////////////////


// END DHW cycle (Automatic)
if (Force_DHW_State == 1 && Sterilization_State == 0 && DHW_Temp >= DHW_Target_Temp)
