var SolarFunction_active            = global.get('SolarFunction_active',"file");
var solar_function_status1           = flow.get('solar_function_status1',"file");
var solar_function_status2           = flow.get('solar_function_status2',"file");
let msg1={};


if (msg.payload == "new_day")
    {
        flow.set('solar_function_status1',undefined,"file");
        flow.set('solar_function_status2',undefined,"file");
        return null;
    }
else
{

    if (SolarFunction_active == 0) 
        {
            msg1.payload = "Idle";
            msg1.topic = 'Solar function status';
            msg1.color = "";
            return [msg1];
        }

    if (SolarFunction_active == 1 && solar_function_status1 === undefined)  
        {
            msg1.payload = "ACTIVE (" + msg.payload + ")";
            msg1.topic = 'Solar function status';
            msg1.color = "gold";

            flow.set('solar_function_status1',msg1.payload,"file");
            return [msg1];
        }


    if (SolarFunction_active == 2 && solar_function_status2 === undefined) 
        {
            msg1.payload = "Completed (" + msg.payload + ")";
            msg1.topic = 'Solar function status';
            msg1.color = "green";
            flow.set('solar_function_status2',msg1.payload,"file");

            return [msg1];
        }
}

var value = msg.payload;
if (value < 0) {value = -1 * value;}        // get rid of a minus 
value = value + 600;                        // manipulate the rounding a bit
value = Math.round(value/1000)*1000;        // the rounding itself





var current_gauge_limits = flow.get('solar_gauge_min_max_value');
if (current_gauge_limits === undefined)
    {
        current_gauge_limits = flow.get('solar_gauge_min_max_value',"file");
    }

if (value > current_gauge_limits || current_gauge_limits === undefined)
    {
    flow.set('solar_gauge_min_max_value',value,"file");
    flow.set('solar_gauge_min_max_value',value);
    }

let msg1={};
let msg2={};
let msg3={};
let msg4={};
let msg5={};


msg1 = {ui_control:{max:current_gauge_limits}};
msg2 = {ui_control:{min:(-1 * current_gauge_limits)}};
msg3 = {ui_control:{seg1:0, seg2:0}};
msg4 = {ui_control:{ymin:(-1 * current_gauge_limits)}};
msg5 = {ui_control:{ymax:current_gauge_limits}};



return [msg1,msg2,msg3,msg4,msg5];

msg.payload = Number(msg.payload).toFixed(0);
msg.payload = parseFloat(msg.payload);

if (msg.payload < 0)
{
    msg.topic = 'Production'
}

if (msg.payload > 0)
{
    msg.topic = 'Consumption'
}
if (msg.payload === NaN)
