}

var selectedSensor = global.get('HW_sensors.sensor' + i, "file");
if (selectedSensor === undefined) { return null }
if (msg.topic != selectedSensor) { return null }

let msg2 = {};
msg2.payload = msg.payload;
msg2.payload = Number(msg2.payload).toFixed(2);
msg2.payload = parseFloat(msg2.payload);

msg2.destination = 'WAR'; 
node.send([null, msg2, msg2]);

var HW_sensors = global.get('HW_sensors', "file");
if (HW_sensors === undefined) {return null}

let msg1={};

for (let i = 1; i < 16; i++){

    var sensor = global.get('HW_sensors.sensor'+i,"file")
    if (sensor !== undefined) {
        msg1.payload = sensor;
        msg1.source = i;
        var opt = flow.get('mqtt_sensor'+i,"file");
        msg1.options = opt;
        msg1.tooltip = '<font size= 4px >' + sensor + '</font>';

        node.send(msg1)
    }
}

