        var rtc_t7_custom_cor = F_RTC.z1.t7_correction_value;

        if (isNaN(rtc_t1 + rtc_t2 + rtc_t3 + rtc_t4 + rtc_t5 + rtc_t6 + rtc_t7) === true)               // if the variables are added to eachother and the result is Not a Number, produce an error message.
        {
