            return [null, null, msg99, null];

        }

        // ADJUST THE VALUES BELOW TO YOUR OWN SITUATION
/*
        if (rtc_dT >= rtc_t1) { rtc_correction = rtc_correction - 1; }                 // It is way too hot.
        if (rtc_dT >= rtc_t2) { rtc_correction = rtc_correction - 1; }                 // It is too hot.
        if (rtc_dT >= rtc_t3) { rtc_correction = rtc_correction - 1; }                 // It is a little too hot.

        if (rtc_dT < rtc_t3 && rtc_dT > rtc_t4) { rtc_correction = 0; }               // It is right in the middle.... Nice

        if (rtc_dT <= rtc_t4) { rtc_correction = rtc_correction + 1; }                 // It is a little too cold
        if (rtc_dT <= rtc_t5) { rtc_correction = rtc_correction + 1; }                 // It is a little a fair bit colder
        if (rtc_dT <= rtc_t6) { rtc_correction = 3; }                 // It is a little way too cold
*/
        if (rtc_dT >= rtc_t1)                        { rtc_correction = F_RTC.z1.t1_correction; } // It is way too hot.
        if (rtc_dT >= rtc_t2 && rtc_correction == 0) { rtc_correction = F_RTC.z1.t2_correction; } // It is too hot.
        if (rtc_dT >= rtc_t3 && rtc_correction == 0) { rtc_correction = F_RTC.z1.t3_correction; } // It is a little too hot.

        if (rtc_dT <= rtc_t6 && rtc_correction == 0) { rtc_correction = F_RTC.z1.t6_correction; } // It is a little way too cold
        if (rtc_dT <= rtc_t5 && rtc_correction == 0) { rtc_correction = F_RTC.z1.t5_correction; } // It is a little a fair bit colder
        if (rtc_dT <= rtc_t4 && rtc_correction == 0) { rtc_correction = F_RTC.z1.t4_correction; } // It is a little too cold


        // Use incremental_setpoints if switch enabled
        var F_RTC_z1_incremental_setpoints_state = global.get('F_RTC.z1.incremental_setpoints_state', "file");    //switch on/off
        if (F_RTC_z1_incremental_setpoints_state === undefined) { F_RTC_z1_incremental_setpoints_state = 0; }
        if ((rtc_dT < rtc_t6 && rtc_dT > rtc_t7) && F_RTC_z1_incremental_setpoints_state == 1)                // in range (between +3 and +custom)
        {
            // calculate correction (between +3 and +custom):
            rtc_correction = rtc_t7_custom_cor - F_RTC.z1.t1_correction;                                 // example (15-3)=12 degrees
            // calculate temp range:
            var Range = (rtc_t6 - rtc_t7); // -2.2 - -4.0 = 1.8                     //calculate No. steps    0.1 degrees steps?
            Range = (Range * 10);                                                   // 18 steps
            // devide rtc_correction through steps:
            rtc_correction = (rtc_correction / Range);                              // 12 / 18 = 0.67 degrees-steps
            // Calculate actual step:
            var Actual_step = (rtc_t6 - rtc_dT) * 10;                               // stap 17 van 18
            Actual_step = Number(Actual_step).toFixed(0);
            // Apply correction at this step:
            rtc_correction = (Actual_step * rtc_correction);                        // 18 * 0.67 = 11.32
            rtc_correction = Number(rtc_correction).toFixed(1);                     // 11,3
            rtc_correction = parseFloat(rtc_correction);
            rtc_correction = rtc_correction + F_RTC.z1.t1_correction;                                     // 11.3 + 3 = 14.3
            rtc_correction = Number(rtc_correction).toFixed(1);
            rtc_correction = parseFloat(rtc_correction);
