        global.set('NightReductionWaterTemp.correction',0,"file");
    }

    var NR_W_state = global.get('NightReductionWaterTemp.state',"file");
    if (NR_W_state === undefined) {
        NR_W_state=0;
        global.set('NightReductionWaterTemp.current_value',0,"file");
    }


    ////////////////////////////////////////////////////////////////////////////////

    let msg1={}, msg99={};
    msg1.payload = nr_correction; 
    msg1.topic = "nr_correction";

    global.set('NightReductionWaterTemp.current_value',msg1.payload,"file");


    msg99.payload = "New correction: " + nr_correction + " °C"; 
    msg99.topic = "Function NightReduction";

    return [msg1,msg99];
}

////////////////////////////////////////////////////////////////////////////
////////////////////////// RTC - ON/OFF function ///////////////////////////
//
//
//  This function has the goal to act when the temperature in the room is too high.
//  It can either turn off the heatpump, or take the [heat]-mode out of the current operating mode.
//  

var MQTT = global.get('MQTT', "file");
if (MQTT.block_active == 0 || (MQTT.block_active == 1 && MQTT.block_mode == 2 && MQTT.allow_rtc_onoff == 1)) {

    var msg1 = {};        //  pin 1 - Output to power pump on/off 
    msg1.source = "flow";

    var msg2 = {};        //  pin 2 - Output to Operating Mode
    var msg99 = {};        //  pin 3 - Messages to Log
    //
    //  return [msg1,msg2,msg99]

    var TOP69_Sterilization_State = global.get('TOP69_Sterilization_State', "file");

    var Sterilization_active = global.get('Sterilization_active', "file");
    if (Sterilization_active === undefined) { Sterilization_active = 0; }

    var Force_DHW_active = global.get('Force_DHW_active', "file");
    if (Force_DHW_active === undefined) { Force_DHW_active = 0; }

    // var VirtualState = global.get('VirtualState'); //used to prevent unwanted shutdown detection 

    var Operating_Mode = global.get('TOP4_Operating_Mode_State', "file");
    if (Operating_Mode === undefined) { return null; }
    //  Operating mode (0=Heat only, 1=Cool only, 2=Auto(Heat), 3=DHW only, 4=Heat+DHW, 5=Cool+DHW, 6=Auto(Heat)+DHW, 7=Auto(Cool), 8=Auto(Cool)+DHW)

    var F_RTC = global.get('F_RTC', "file");

    //
    if (F_RTC.automation_power_state === undefined) { F_RTC.automation_power_state = 0; }          // Get the global variable. Used to see if the RTC function is enabled through GUI.
    if (F_RTC.automation_OM_state === undefined) { F_RTC.automation_OM_state = 0; }                // Get the global variable. Used to see if the RTC function is enabled through GUI.

    var F_SOLAR = global.get("F_SOLAR", "file");
    if (F_SOLAR === undefined) {F_SOLAR={};}
    if (F_SOLAR.function_active_state === undefined) { F_SOLAR.function_active_state = 0; }
    //////////////////////////////////////////////////////////////////////////////////////////////////////
    //////////////////////////////////////////////////////////////////////////////////////////////////////

    // Operating mode (0=Heat only, 1=Cool only, 2=Auto(Heat), 3=DHW only, 4=Heat+DHW, 5=Cool+DHW, 6=Auto(Heat)+DHW, 7=Auto(Cool), 8=Auto(Cool)+DHW)    
    if ((F_RTC.automation_power_state == 0 && F_RTC.automation_OM_state == 0) || Operating_Mode === undefined || Operating_Mode == 1 || Operating_Mode == 5 || Operating_Mode == 7 || Operating_Mode == 8 || Sterilization_active == 1 || Force_DHW_active == 1 || F_SOLAR.function_active_state == 1 || TOP69_Sterilization_State == 1)     //  First IF
    {
        //global.set('F_RTC.on_off_status',undefined,"file");
        return null;
    }



    else {
        // Declaration of variables including their values.    
        var rtc_PV = global.get('T_room');                             // Get the global variable. THe room temperature for RoomTemperatureCompensation you want to control.
        if (rtc_PV === undefined) { return null; }
        var rtc_SP = global.get('WoonkamerSP', "file");                 // Virtual target temperature setpoint for RTC. Set in Dashboard. 
        if (rtc_SP === undefined) { return null; }
        var rtc_dT = (rtc_PV - rtc_SP);                                  // local variable for calculated temperature difference. If > 0 it is too warm, If < 0 it is too cold. 

        var TOP0_Heatpump_State = global.get('TOP0_Heatpump_State', "file");              // get current state of the heatpump. on or off (1 or 0)
        // below are the variables containing the temperature ranges.
        var rtc_off = F_RTC.automation_off_limit;                               // Range turn heatpump off
        if (rtc_off === undefined) { return null; }
        var rtc_on = F_RTC.automation_on_limit;                               // Range turn heatpump on
        if (rtc_on === undefined) { return null; }

        var F_stored_next_operating_mode = global.get('F_stored_next_operating_mode', "file");

        msg99.topic = "Function RTC";

        //*****************************************************************************
        //////////////////////////////////////////////////////////////////////////////////////////////////////
        //////////////////////////////////////////////////////////////////////////////////////////////////////
        // Operating mode (0=Heat only, 1=Cool only, 2=Auto(Heat), 3=DHW only, 4=Heat+DHW, 5=Cool+DHW, 6=Auto(Heat)+DHW, 7=Auto(Cool), 8=Auto(Cool)+DHW)
        if (rtc_off > rtc_on) {
            //node.warn('RTC function: Current OFF temperature is lower than the ON temperature. You must correct this !! ');
            msg99.payload = "<b>ERROR:</b> Current switch-OFF-temperature is lower than the switch-ON-temperature. You must correct this !!";
            return [null, null, msg99];
        }

        //if (rtc_dT > rtc_off)                           {global.set('F_RTC.on_off_status',0,"file");}
        //if (rtc_dT >= rtc_on && rtc_dT <= rtc_off)      {global.set('F_RTC.on_off_status',1,"file");}
        //if (rtc_dT < rtc_on)                            {global.set('F_RTC.on_off_status',2,"file");}


        //*****************************************************************************
        //      Scenario 1: the roomtemperature is high and above the 'off'limit. Automation start
        //*****************************************************************************
        if (rtc_dT > rtc_on && TOP0_Heatpump_State == 1 && Operating_Mode != 3) {
            //  Operating mode (0=Heat only, 1=Cool only, 2=Auto(Heat), 3=DHW only, 4=Heat+DHW, 5=Cool+DHW, 6=Auto(Heat)+DHW, 7=Auto(Cool), 8=Auto(Cool)+DHW)
            if (Operating_Mode == 2 && F_RTC.automation_power_state == 1) {

                msg1.payload = 0;
                msg1.source = "flow";
                msg1.topic = "new_heatpump_power";
                msg99.payload = "Automation 'Turn off/on power' activated - power OFF (Room temp: " + rtc_PV + "°C)";
                global.set('MQTT_Source', "rtc automation: on/off");
                global.set('F_RTC.automation_active', 1, "file");
                return [msg1, null, msg99];
            }

            if (Operating_Mode == 0 && F_RTC.automation_power_state == 1) {
                global.set('F_RTC.automation_active', 1, "file");
                msg1.payload = 0;
                msg1.source = "flow";
                msg1.topic = "new_heatpump_power";
                msg99.payload = "Automation 'Turn off/on power' triggered - power OFF (Room temp: " + rtc_PV + "°C)";
                global.set('MQTT_Source', "rtc automation: on/off");
                return [msg1, null, msg99];
            }

            if ((Operating_Mode == 4 || Operating_Mode == 6) && F_RTC.automation_OM_state == 1) {
                if (F_stored_next_operating_mode === undefined) {
                    global.set('F_stored_next_operating_mode', Operating_Mode, "file");
                    msg99.payload = 'Stored next operating mode is set to ' + Operating_Mode; node.send([null, null, msg99]); 
                }
                global.set('F_RTC.automation_active', 1, "file");
                msg2.payload = 3;
                msg2.topic = "new_Operating_Mode";
                if (Operating_Mode == 4) { Operating_Mode = "Heat+DHW"; }
                if (Operating_Mode == 6) { Operating_Mode = "Auto+DHW"; }
                msg99.payload = "Automation 'Change operating mode' triggered - Operating mode from " + Operating_Mode + " to DHW only";
                global.set('MQTT_Source', "rtc automation: on/off");
                return [null, msg2, msg99];
            }
        }
        //*****************************************************************************
        //      Scenario 2: the roomtemperature is low enough again. Automation end
        //*****************************************************************************
        if (rtc_dT < rtc_off && F_RTC.automation_active == 1) {
            if (TOP0_Heatpump_State == 0) {
                global.set('F_RTC.automation_active', 0, "file");
                msg1.payload = 1;
                msg1.source = "flow";
                msg1.topic = "new_heatpump_power";
                msg99.payload = "Automation 'Turn off/on power' triggered - power ON (Room temp: " + rtc_PV + "°C)";
                global.set('MQTT_Source', "rtc automation: on/off");
                return [msg1, null, msg99];
            }
            if (TOP0_Heatpump_State == 1) {
                global.set('F_RTC.automation_active', 0, "file");
                var OM; // create empty
                if (F_stored_next_operating_mode === undefined) {   
                    //  READ: Operating mode (0=Heat only, 1=Cool only, 2=Auto(Heat), 3=DHW only, 4=Heat+DHW, 5=Cool+DHW, 6=Auto(Heat)+DHW, 7=Auto(Cool), 8=Auto(Cool)+DHW) 
                    //  SET:  Operating mode (0=Heat only, 1=Cool only, 2=Auto, 3=DHW only, 4=Heat+DHW, 5=Cool+DHW, 6=Auto+DHW
                    if (Operating_Mode == 0) { F_stored_next_operating_mode = 0; }
                    if (Operating_Mode == 1) { F_stored_next_operating_mode = 1; }
                    if (Operating_Mode == 2) { F_stored_next_operating_mode = 2; }
                    if (Operating_Mode == 3) { F_stored_next_operating_mode = 4; }
                    if (Operating_Mode == 4) { F_stored_next_operating_mode = 4; }
                    if (Operating_Mode == 5) { F_stored_next_operating_mode = 5; }
                    if (Operating_Mode == 6) { F_stored_next_operating_mode = 6; }
                    if (Operating_Mode == 7) { F_stored_next_operating_mode = 2; }
                    if (Operating_Mode == 8) { F_stored_next_operating_mode = 6; }

                    OM = F_stored_next_operating_mode;
                    if (OM == 0) { OM = "Heat only"; } if (OM == 1) { OM = "Cool only"; } if (OM == 2) { OM = "Auto"; } if (OM == 3) { OM = "DHW only"; } if (OM == 4) { OM = "Heat+DHW"; } if (OM == 5) { OM = "Cool+DHW"; } if (OM == 6) { OM = "Auto+DHW"; } if (OM == 7) { OM = "Auto"; } if (OM == 8) { OM = "Auto+DHW"; }
                    msg99.payload = "Automation 'Change operating mode' triggered - Operation mode set to " + F_stored_next_operating_mode + " (" + OM + ")";
                    node.send([null, null, msg99])
                }
                else {
                    OM = F_stored_next_operating_mode;
                    if (OM == 0) { OM = "Heat only"; } if (OM == 1) { OM = "Cool only"; } if (OM == 2) { OM = "Auto"; } if (OM == 3) { OM = "DHW only"; } if (OM == 4) { OM = "Heat+DHW"; } if (OM == 5) { OM = "Cool+DHW"; } if (OM == 6) { OM = "Auto+DHW"; } if (OM == 7) { OM = "Auto"; } if (OM == 8) { OM = "Auto+DHW"; }
                    msg99.payload = "Automation 'Change operating mode' triggered - Operation mode restored to " + F_stored_next_operating_mode + " (" + OM + ")";
                    node.send([null, null, msg99])
                }
                msg2.payload = F_stored_next_operating_mode;
                msg2.topic = "new_Operating_Mode";

                global.set('F_stored_next_operating_mode', undefined, "file");
                msg99.payload = 'Stored next operating mode is removed ' ; node.send([null, null, msg99]); 

                //if (global.get('MQTT_Source') === undefined) { global.set('MQTT_Source', "rtc on/off"); }
                return [null, msg2, null];
            }

        }
        //******************************************************************************
    }
}

