                if (TOP4_Operating_Mode_State == 8) { new_Operating_Mode_State = 6; old_Operating_Mode_State = 6 } // prevent undefined new_Operating_Mode_State

                var tag;    // declare variable for tag in log line.
                if (F_SOLAR.detection_method == "kW") { tag = 'kW'; }
                if (F_SOLAR.detection_method == "kWh_prod") { tag = "Today's export (kWh)"; }
                if (F_SOLAR.detection_method == "kWh_result") { tag = "kWh todays result"; }

                if (TOP0_Heatpump_State == 0 && F_SOLAR.power_override == 0) {
                    msg99.payload = "Solar threshold reached but no action fired! (Heat pump is off, 'Allow switch on heat pump' is disabled.)";
                    return ([null, null, null, msg99]);
                }

                if (F_stored_next_operating_mode === undefined) {
                    global.set('F_stored_next_operating_mode', old_Operating_Mode_State, "file");
                    msg99.payload = 'Stored next operating mode is set to ' + old_Operating_Mode_State ; node.send([null,null,null,msg99]); 

                }

                // msg3///////////////////////////////////////////////////////////////////////////////////////////////////////
                // Turn on heatpump in case it is off
                if (TOP0_Heatpump_State == 0 && F_SOLAR.power_override == 1) {
                    msg99.payload = "Turn on Heatpump";
                    msg3.payload = 1; msg3.source = "flow";                                           // send 1 to turn on the heatpump
                    if (global.get('MQTT_Source') === undefined) { global.set('MQTT_Source', "Solar"); }
                    node.send([null, null, msg3, null])
                }



                // msg1///////////////////////////////////////////////////////////////////////////////////////////////////////
                // Set DHW setpoint to high
                if (DHW_Current_SP != F_SOLAR.dhw_sp_high) {
                    msg1.payload = F_SOLAR.dhw_sp_high;
                    msg99.payload = "triggered threshold (" + tag + "). DHW SP to " + F_SOLAR.dhw_sp_high;
                    node.send([msg1, null, null, msg99]);
                }

                // msg2///////////////////////////////////////////////////////////////////////////////////////////////////////
                // Set Operation Mode
                if (new_Operating_Mode_State != old_Operating_Mode_State && old_Operating_Mode_State !== undefined) {
                    msg2.payload = new_Operating_Mode_State;
                    msg99.payload = "Operation mode changed from " + old_Operating_Mode_State + " to " + new_Operating_Mode_State;
                    node.send([null, msg2, null, msg99]);
                }
                global.set('F_RTC.automation_active', 0, "file");
                if (F_SOLAR.function_active_state != 1) {
                    global.set("F_SOLAR.function_active_state", 1, "file");
                    if (global.get('MQTT_Source') === undefined) { global.set('MQTT_Source', "Solar"); }
                    return null;
                }
            }




            // ACTIONS WHEN THE SOLAR FUNCTION IS TRIGGERED FOR ENDING.
            //////////////////////////////////////////////////////////////////////////////////////////////////////////////////
            //
            if (msg.payload > 0 && F_SOLAR.detection_method == "kW" && DHW_Current_SP == F_SOLAR.dhw_sp_high && F_SOLAR.fallback == 1 && F_SOLAR.function_active_state == 1) {
                if (global.get('MQTT_Source') === undefined) { global.set('MQTT_Source', "Solar"); }
                msg1.payload = F_SOLAR.dhw_sp_low;
                msg99.payload = "triggered threshold (" + tag + "). Above 0kWh threshold. DHW SP to " + F_SOLAR.dhw_sp_low;
                global.set("F_SOLAR.function_active_state", 0, "file");
                node.send([msg1, null, null, msg99]);
                return null;
            }

            //TOP9_DHW_Target_Temp
            // Operating mode (0=Heat only, 1=Cool only, 2=Auto(Heat), 3=DHW only, 4=Heat+DHW, 5=Cool+DHW, 6=Auto(Heat)+DHW, 7=Auto(Cool), 8=Auto(Cool)+DHW)

            var TOP10_DHW_Temp = global.get('TOP10_DHW_Temp');
            var TOP20_ThreeWay_Valve_State = global.get('TOP20_ThreeWay_Valve_State');       // threewayvalvue 1 = DHW, 0 = HEAT

            if (TOP10_DHW_Temp >= F_SOLAR.dhw_sp_high && F_SOLAR.function_active_state == 1) {
                /////////////////////////// 

                msg99.payload = "DHW temperature has reached SP high";
                node.send([null, null, null, msg99])

                let myTimeout = setTimeout(f_rollbackaction, F_SOLAR.delay_after_completion * 60 * 1000);                 // <<<<<<<<<<<<<<<<  DELAY 
                node.status({ fill: "red", shape: "ring", text: "" });

                function f_rollbackaction() {
                    var F_stored_next_operating_mode = global.get('F_stored_next_operating_mode', "file");

                    var F_SOLAR = global.get('F_SOLAR', "file");
                    var DHW_Current_SP = global.get('TOP9_DHW_Target_Temp', "file");
                    if (F_SOLAR.function_active_state == 2) { return null; }

                    global.set("F_SOLAR.function_active_state", 2, "file");

                    msg99.payload = "Cooldown period (" + F_SOLAR.delay_after_completion + " minutes) has ended. Solar function done for today";
                    node.send([null, null, null, msg99]);
                    ///////////////////////////////////////////////////
                    if (F_stored_next_operating_mode !== undefined) {
                        TOP4_Operating_Mode_State = global.get('TOP4_Operating_Mode_State', "file")
                        if (TOP4_Operating_Mode_State != F_stored_next_operating_mode) {
                            msg2.payload = F_stored_next_operating_mode;
                            if (global.get('MQTT_Source') === undefined) { global.set('MQTT_Source', "Solar"); }
                            msg99.payload = "Restoring previous OperatingMode: " + msg2.payload;
                            node.send([null, msg2, null, msg99]);
                        }
                        global.set('F_stored_next_operating_mode', undefined, "file");
                        msg99.payload = 'Stored next operating mode is removed'; node.send([null, null, null, msg99]); 
                    }
                    if (DHW_Current_SP != F_SOLAR.dhw_sp_low) {
                        msg1.payload = F_SOLAR.dhw_sp_low;
                        msg99.payload = "Restoring DHW setpoint to: " + F_SOLAR.dhw_sp_low + "°C";
                        if (global.get('MQTT_Source') === undefined) { global.set('MQTT_Source', "Solar"); }
                        node.send([msg1, null, null, msg99]);
                    }
                    clearTimeout(myTimeout);
                    node.status({});
                }   ////////////// end of f_rollbackaction
            }
        }
    }
}




