var war_Tb          = global.get('T_outside',"file");                           // Outdoor temperature.
var SP_war          = global.get('SP_war');                                     // Setpoint after the WAR calculation done in (other) function.

var war_ot1         = global.get('war_ot1',"file");
var war_ot2         = global.get('war_ot2',"file");
var war_ot3         = global.get('war_ot3',"file");

var war_wt1         = global.get('war_wt1',"file");
var war_wt2         = global.get('war_wt2',"file");
var war_wt3         = global.get('war_wt3',"file");

// Design example
//          OUTSIDE                 WATER
//          Temperature     >>      Temperature
//----------------------------------------------------------------------------
// Range 1
//          (OT1=)  20      >>  25  (=WT1)
// Range 2
//          (OT2=)  5       >>  31  (=WT2)
// Range 3
//          (OT3=)  -10     >>  37  (=WT3)
// Range 4
//----------------------------------------------------------------------------
//
//
// math part 
// Y = aX + b
// X axis = ot variable
// Y axis = wt variable
var a;
var b;

if (war_Tb <= war_ot3)                                                          // Range 1 in the graph
    {   a = 0;
        b = war_wt3;
    }

if (war_Tb > war_ot2)                                                           // Range 2 in the graph
    {   a = ((war_wt2-war_wt1)/(war_ot2-war_ot1));
        b = (war_wt1-(war_ot1*a));
    }

if (war_Tb <= war_ot2)                                                          // Range 3 in the graph
    {   a = ((war_wt3-war_wt2)/(war_ot3-war_ot2));
        b = (war_wt2-(war_ot2*a));
    }

if (war_Tb >= war_ot1)                                                          // Range 4 in the graph
    {   a = 0;
        b = war_wt1;
    }

// Chart part. 
// Here the chart is created with the variables.
// Part 1 of the chart plots the WAR temperature profile set by the values on the page. 
// Part 2 of the chart plots the line which represents the actual temperature to water temperature. To read it easier.
// Part 3 of the chart plots the ranges so the chart will be more stable on screen, not messing around with chart axis.

var chart =     [{
    "series":   ["Water","Actual",''],
    "data": [
                [
                {"x":(war_ot1+15),"y":war_wt1},                                 // chart part 1: WAR program series
                {"x":(war_ot1),"y":war_wt1},
                {"x":(war_ot2),"y":war_wt2},
                {"x":(war_ot3),"y":war_wt3},
                {"x":(war_ot3-15),"y":war_wt3}
                ],

                [
                {"x":(war_ot3-15),  "y":((a*war_Tb)+b)},                        // chart part 2: Actual lines which make use of the function above.
                {"x":(war_Tb),      "y":((a*war_Tb)+b)},
                {"x":(war_Tb),      "y":war_wt1-5},
                ],

                [                                                               // chart part 3: X-Y as with fixed ranges to make it look more stable.
                {"x":(war_ot3-15),"y":(war_wt3+5)},
                {"x":(war_ot3-15),"y":(war_wt1-5)},
                {"x":(war_ot1+15),"y":(war_wt1-5)}
                ]

            ],    
    "labels":[ "","" ]
}];



let msg1={}, msg2={}, msg3={};
msg.payload = chart; msg2.payload = war_Tb; msg3.payload = SP_war

msg2.payload = Number(msg2.payload).toFixed(1);
msg2.payload = parseFloat(msg2.payload);
msg2.topic='T outside';

msg3.payload = Number(msg3.payload).toFixed(1);
msg3.payload = parseFloat(msg3.payload);
msg3.topic='SP WAR';

return [msg, msg2, msg3];

