//
//      [SP0_start]                 = Value is set through the dashboard. Compares to a virtual Ta Direct.
//      
//      [correction_war]            = calculate a correction in this function if it is enabled
//      [SP1_war]                   = [SP0_start] + [correction_war]
//
//      [correction_rtc]            = calculate a correction in this function if it is enabled
//      [SP2_rtc]                   = [SP1_war] + [correction_rtc]
//
//      [correction_softstart]      = calculate a correction in this function if it is enabled
//      [SP3_softstart]             = [SP2_rtc] + [Correction_softstart]
//
//      [SP4_night_reduction]       = [SP3_softstart] + [NightReduction_current_value]
//
//      [SP_Final]                  = [SP4_night_reduction] = New real SP to [SET5 SetZ1HeatRequestTemperature]
//
//////////////////////////////////////////////////////////////////////////////
//////////////////////////////////////////////////////////////////////////////

// All these functions ONLY need to be executed when the heatpump is in HEAT mode. 
var ThreeWay_Valve_State=global.get('ThreeWay_Valve_State');                    // Get the global variable for current possition of the three way valve. ROOM or DHW mode. (0=HEAT / 1=DHW)

if (ThreeWay_Valve_State==1){msg.payload=0;return msg;}                         // If state is 1 (DHW mode), correction 0 is direclty exported to the message and the function ends here.
else
{
    ////////////////////////////////////////////////////////////////////////////
    ////////////////////////////////////////////////////////////////////////////

    var SP0_start       = global.get('SP_DIRECT_virt',"file");                  // Get the global variable for SP_DIRECT_virt. Value is set through the dashboard
    if (global.get("SP_DIRECT_virt","file") === undefined) 
        {
        throw "Virtual SP (Ta) is not correctly set in Dashboards. Please set it."
        }


    //////////////////////////////// WAR /////////////////////////////////////////
    // This function replaces the virtual Ta Direct set from the dashboard. 
    //
    // Design example
    //          OUTSIDE                 WATER
    //          Temperature     >>      Temperature
    //----------------------------------------------------------------------------
    // Range 1
    //          (OT1=)  20      >>  25  (=WT1)
    // Range 2
    //          (OT2=)  5       >>  31  (=WT2)
    //----------------------------------------------------------------------------
    //          (OT2=)  5       >>  31  (=WT2)
    // Range 3
    //          (OT3=)  -10     >>  37  (=WT3)
    // Range 4
    //----------------------------------------------------------------------------
    var war_state       = global.get('Correction_0_WAR_on_off',"file");             // Get the global variable. Used to see if the WAR function is enabled.
    var war_Tb          = global.get('T_outside',"file");                           // Outdoor temperature.
    if (isNaN(war_Tb))               // if the variable is Not a Number, produce an error message.
            {throw "WAR function: T_outside is not valid. Please correct this in the flow-configuration screen of NodeRed."}
    var SP1_war                                                                     // set variable

    if (war_state==1 && war_Tb > -50)                                               // If the state is 1 (enabled), AND outside temperature contains a value.
                                                                                    // Only if above conditions are met, we will go through the calculation.
        {
            var war_ot1         = global.get('war_ot1',"file");                 // Outside temperature high         (warm outside)
            var war_ot2         = global.get('war_ot2',"file");                 // Outside temperature intermediate (intermediate outside temperature)
            var war_ot3         = global.get('war_ot3',"file");                 // Outside temperature low          (cold outside)
            var war_wt1         = global.get('war_wt1',"file");                 // Water temperature low            (cold water)
            var war_wt2         = global.get('war_wt2',"file");                 // Water temperature intermediate   (intermediate water temperature)
            var war_wt3         = global.get('war_wt3',"file");                 // Water temperature high           (warm water)

                if (isNaN(war_ot1+war_ot2+war_ot3+war_wt1+war_wt2+war_wt3))     // if the variables are added to eachother and the result is Not a Number, produce an error message.
                {throw "WAR function temperatures are not correctly set in the dashboard. Please correct this in the GUI."}

            if (war_Tb => war_ot1) 
                {SP1_war = war_wt1;}                                        // Range 1

            if (war_Tb < war_ot1 && war_Tb > war_ot2)                       // Range 2
                {SP1_war = (((war_wt1-war_wt2)*((war_Tb-war_ot1)/(war_ot1-war_ot2)))+war_wt1);}

            if (war_Tb == war_ot2) {SP1_war = war_wt2;}                     // transition point 2|3

            if (war_Tb < war_ot2 && war_Tb > war_ot3)                       // Range 3
                {SP1_war = (((war_wt2-war_wt3)*((war_Tb-war_ot2)/(war_ot2-war_ot3)))+war_wt2);}

            if (war_Tb <= war_ot3)                                          // Range 4
                {SP1_war = war_wt3;}                                            

            SP1_war = Number(SP1_war).toFixed(0);
            SP1_war = parseFloat(SP1_war);
        }

    else
        {
            SP1_war = SP0_start;                                                // Get the global variable for SP_DIRECT_virt. Value is set through the dashboard
        }


    ////////////////////////////////////////////////////////////////////////////
    //////////////////////////////// RTC ///////////////////////////////////////
    //
    // Automagical RoomTemperatureCompensation (=RTC) (original StooklijnDelta by CurlyMo, translated to NodeRed by EdterBak)
    // This function checks a virtual setpoint of a room against a measured temperature of the room.
    //
    //
    // [Function explanation]
    // First check: Is the function enabled (through GUI/Dashboard). If not, correction will be 0.
    // When enabled; Each IF checks the value of dT_rtc against an offset. If the check is true, the local variable "Correction_1_RTC" is corrected by -1 or +1.
    // Additionally. If TWO IFs are true, then the correction is added twice. 
    // Eg. It is too hot inside (PV). When it is 1.5C too warm, the RoomTemperatureCompensation variable will be -3.
    //
    //    
    // If the RTC function is disabled, the correction is set to 0 so it is also visible as 0 in the dashboard.    
    //

    var rtc_state       = global.get('Correction_1_RTC_on_off',"file");         // Get the global variable. Used to see if the RTC function is enabled.

    if (rtc_state == 0) {rtc_correction = 0;}                                   // If RTC is disabled, set correction to 0. end if
    else
        {

        // Declaration of variables including their values.    
        var rtc_correction  = 0;                                                // declare local variable, start with value zero
        var rtc_PV          = global.get('T_woonkamer_BT');                     // Get the global variable. THe room temperature for RoomTemperatureCompensation you want to control.
        var rtc_SP          = global.get('WoonkamerSP',"file");                 // Virtual target temperature setpoint for RTC. Set in Dashboard. 
        var rtc_Tb          = global.get('T_outside',"file");                   // Outdoor temperature.
        var rtc_dT          = (rtc_PV-rtc_SP);                                  // local variable for calculated temperature difference. If > 0 it is too warm, If < 0 it is too cold. 

        //
        // below are the variables containing the temperature ranges.
        var rtc_t1          = global.get('rtc_t1',"file");                      // Range +3
        var rtc_t2          = global.get('rtc_t2',"file");                      // Range +2
        var rtc_t3          = global.get('rtc_t3',"file");                      // Range +1
        var rtc_t4          = global.get('rtc_t4',"file");                      // Range -1
        var rtc_t5          = global.get('rtc_t5',"file");                      // Range -2
        var rtc_t6          = global.get('rtc_t6',"file");                      // Range -3

            if (isNaN(rtc_t1+rtc_t2+rtc_t3+rtc_t4+rtc_t5+rtc_t6))               // if the variables are added to eachother and the result is Not a Number, produce an error message.
            {throw "RTC function temperatures are not correctly set in the dashboard. Please correct this in the GUI."}

        // ADJUST THE VALUES BELOW TO YOUR OWN SITUATION
        if (rtc_dT >= rtc_t3) {rtc_correction = rtc_correction -1;}             // It is a little too hot.
        if (rtc_dT >= rtc_t2) {rtc_correction = rtc_correction -1;}             // It is too hot.
        if (rtc_dT >= rtc_t1) {rtc_correction = rtc_correction -1;}             // It is way too hot.

        if (rtc_dT < rtc_t4) {rtc_correction = rtc_correction +1;}              // It is a little too cold
        if (rtc_Tb < 18)                                                        // Extra check to justify increasing the setpoint this much.
            {
            if (rtc_dT < rtc_t5) {rtc_correction = rtc_correction +1;}          // It is too cold.
            if (rtc_dT < rtc_t6) {rtc_correction = rtc_correction +1;}          // It is way too cold
            }
        }



    ////////////////////////////////////////////////////////////////////////////////
    //
    //      [SP0_start]                 = Value is set through the dashboard. Compares to a virtual Ta Direct.
    //      
    //      [correction_war]            = calculate a correction in this function if it is enabled
    //      [SP1_war]                   = [SP0_start] + [correction_war]
    //
    //      [correction_rtc]            = calculate a correction in this function if it is enabled
    //      [SP2_rtc]                   = [SP1_war] + [correction_rtc]
    //
    //      [correction_softstart]      = calculate a correction in this function if it is enabled
    //      [SP3_softstart]             = [SP2_rtc] + [Correction_softstart]
    //
    //      [SP_Final]                  = [SP3_softstart] = New real SP to [SET5 SetZ1HeatRequestTemperature]
    //
    var SP2_rtc = SP1_war + rtc_correction;





    //////////////////////////////// SOFTSTART /////////////////////////////////////
    // Declaration of variables including their values.
    var ss_state            = global.get('Correction_2_softstart_on_off',"file");          // Get the global variable. Used to see if the softstart function is enabled.
    if (ss_state == 0) {ss_correction = 0;} 

    else 
        {
            var ss_PV_Ta            = global.get('Main_Outlet_Temp');               // PV (Ta/outlet) Temperature of water.
            var ss_PV_Tr            = global.get('Main_Inlet_Temp');                // PV (Tr/inlet) Temperature of water.            
            var ss_dTmax            = 2;                                            // Maximum alowed temperature difference
            var ss_Tb               = global.get('T_outside',"file");               // Outdoor temperature.
                if (isNaN(ss_Tb))               // if the variable is Not a Number, produce an error message.
                {throw "SoftStart function: T_outside is not valid. Please correct this in the flow-configuration screen of NodeRed."}

            var ss_Hz               = global.get('compressor_frequency');           // Get the global variable for current compressor frequency
            var ss_Threshold         = 30;                                           // Hz compressor treshold, initial value. This value is used if in case of error or the values for T_outside is unavailable. 
            var ss_correction_old   = global.get('Correction_2_softstart');         // Get the global variable. The variable correction by softstart function, from memory.
                if (ss_correction_old === undefined) 
                {ss_correction_old = 0;}                                            // make sure there is a value present.
            var ss_correction       = 0;                                            // set initial value new_correction as a basis for the calculation

                    // Treshold calculation based on outside temperatures. Change these values to your own liking.
                    if (ss_Tb >= 3)                     {ss_Threshold=30;}
                    if (ss_Tb < 3 &&  ss_Tb >= 1)       {ss_Threshold=32;}
                    if (ss_Tb < 1 &&  ss_Tb >= -2)      {ss_Threshold=37;}
                    if (ss_Tb < -2 && ss_Tb >= -5)      {ss_Threshold=43;}
                    if (ss_Tb < -5 && ss_Tb >= -7)      {ss_Threshold=46;}
                    if (ss_Tb < -7 && ss_Tb >= -9)      {ss_Threshold=49;}
                    if (ss_Tb < -9 && ss_Tb >= -11)     {ss_Threshold=51;}
                    if (ss_Tb < -11)                    {ss_Threshold=53;}

            // CONDITIONS and calculation for new_correction of the SP.
                // 1 - Compressor frequency must be above 0
                // 2 - Softstart function must be enabled (GUI boolean)
                // 3 - Prevent intervention when the HP is coming back from DHW mode with very hot water. (PVTr < 32) (removed mow)

            if (ss_Hz > 5 && (SP2_rtc - ss_PV_Ta) > ss_dTmax) 
                {ss_correction = (ss_PV_Ta + ss_dTmax - SP2_rtc);
                    if (ss_correction < -4){ss_correction = -4;}
                }

            else
                {ss_correction = 0;} // because the conditions are not met, ss_correction is set to 0.

            // When a new correction value is found, it is compared to the old correction value (= ss_diff). 
            //
                // ss_diff = [new] - [old]
                // ss_diff = [-5]  - [0]  = -5     case 1; Allowed. Startup phase, correction required of -5.
                // ss_diff = [-5]  - [-4] = -1     case 2; Allowed. Not startup phase, but bigger correction required
                // ss_diff = [0]   - [0]  =  0     case 3; Allowed. Current correction the same as old correction.
                // ss_diff = [-5]  - [-5] =  0     case 3; Allowed. Current correction the same as old correction.
                // ss_diff = [-4]  - [-5] = +1     case 4; Restrict. Correction reduction. This is only allowed if rules below are met.
                // ss_diff = [-1]  - [-5] = +4     case 5; Restrict. BIG correction reduction. This is not allowed if rules below are met.

            var ss_diff = (ss_correction - ss_correction_old);

            if (ss_diff <= 0)                                                       // Allowed. case 1, 2 and 3. 
                {ss_correction = ss_correction;}

            if (ss_diff > 0 && ss_Hz > ss_Threshold)                                // Do not allow. Case 4, 5. Check frequency against treshold if it is allow or not. Frequency is too high, so NEW_correction is overwritten with OLD_correction.
                {ss_correction = ss_correction_old;}                        // Keep old correction.    

            if (ss_diff > 0 && ss_Hz <= ss_Threshold)                               // Allow. Restrict. Case 4, 5. Check frequency gainst treshold if it is allow or not. Frequency is low enough, so NEW_correction is kept.
                {ss_correction = ss_correction_old + 1;}                            // Keep new correction. Note: this line can actually be taken out, but does no harm and helps understanding. New_correction is already determined above.
        }

    ////////////////////////////////////////////////////////////////////////////////
    //      [SP1_war]                   = [SP0_Start] + [war_correction]
    //      [SP2_rtc]                   = [SP1_war] + [rtc_correction]
    //      [SP3_Softstart]             = [SP2_trc] + [Correction_Softstart]
    //      [SP_Final]                  = [SP3_Softstart] = New real SP to [SET5 SetZ1HeatRequestTemperature]
    var SP3_ss = SP2_rtc + ss_correction;
    ////////////////////////////////////////////////////////////////////////////////

    ////////////////////////////////////////////////////////////////////////////////
    // Night Reduction
    var nr_corr = global.get('NightReduction_current_value',"file");
    if (nr_corr === undefined)
        {nr_corr = 0;}
    var SP4_nr = SP3_ss + nr_corr;
    ////////////////////////////////////////////////////////////////////////////////


    var SP_Final                        = SP4_nr;



    ////////////////////////////////////////////////////////////////////////////////
    //  Make sure the calculated final setpoint is within the upper and the lower limit.
    var SP_lower_limit                  = global.get('HEAT_SP_lower_limit',"file");
    var SP_upper_limit                  = global.get('HEAT_SP_upper_limit',"file");

    if      (SP_lower_limit === undefined)      {}                                  //
    else    {
                if (SP_Final < SP_lower_limit)      {SP_Final = SP_lower_limit;}    //
            }

    if      (SP_upper_limit === undefined)      {}                                  //
    else    {
                if (SP_Final > SP_upper_limit)      {SP_Final = SP_upper_limit;}    //
            }



    let msg1={}, msg2={}, msg3={}, msg4={}, msg5={}, msg6={}, msg7={}, msg8={}, msg9={}, msg10={};

    msg1.payload = SP0_start;                                                       // not required, for debugging
    msg1.topic='SP0_start';                                                         // not required, for debugging

    msg2.payload = null;                                                            // required
    msg2.topic='empty';                                                             // required

    msg3.payload = SP1_war;                                                         // required
    msg3.topic='SP1_war';                                                           // required

    msg4.payload = rtc_correction;                                                  // not required, for debugging
    msg4.topic = 'rtc_correction';                                                  // not required, for debugging

    msg5.payload = SP2_rtc;                                                         // required
    msg5.topic = 'SP2_rtc';                                                         // required

    msg6.payload = ss_correction;                                                   // required
    msg6.topic = 'ss_correction';                                                   // required

    msg7.payload = SP3_ss;                                                          // not required, for debugging
    msg7.topic = 'SP3_ss';                                                          // not required, for debugging

    msg8.payload = SP_Final;                                                        // required
    msg8.topic = 'SP_Final';                                                        // required

    msg9.payload = nr_corr;
    msg9.topic = 'nightreduction';                                                  // not required, for debugging

    msg10.payload = ss_Hz;
    msg10.topic = 'ss_Hz';                                                          // not required, for debugging


    return [msg1, msg2, msg3, msg4, msg5, msg6, msg7, msg8, msg9, msg10];


}

