var sensorvalue = msg.payload;
for (let i = 0; i < context.global.heishamon.SensorMapping.length; i++) {
    // when sensor is in the global settings 
    msg.sensor = sensor;
    if(sensor == context.global.heishamon.SensorMapping[i][0]){

        return msg;
    }
}
return;

